mapper.select:
select * from blog_type

	WHERE type_name like concat('%',#{param},'%') or alias_name like concat('%',#{param},'%')

limit #{pager.start},#{pager.limit}

SELECT t1.type_id as typeId, t1.type_name as typeName, t1.alias_name as aliasName, t1.sort, t2.articleCount FROM blog_type t1 JOIN (
SELECT DISTINCT bt.type_id, count(ba.article_id) as articleCount FROM blog_article_type bat LEFT JOIN blog_type bt ON bt.type_id = bat.type_id LEFT JOIN blog_article ba ON ba.article_id = bat.article_id GROUP BY bt.type_id
) t2 ON t1.type_id = t2.type_id ORDER BY t1.sort,t2.articleCount desc
